import React from 'react';

const WeatherCard = ({ day, isToday }) => {
    const date = new Date(day.dt * 1000).toLocaleDateString('tr-TR', { weekday: 'long' });

  // Ensure day.temp exists before accessing max and min
  const maxTemp = day.main ? Math.round(day.main.temp_max) : 'N/A';
  const minTemp = day.main ? Math.round(day.main.temp_min) : 'N/A';

  return (
    <div className={`weather-card ${isToday ? 'today' : ''}`}>
      <h3>{date}</h3>
      <img
        src={`http://openweathermap.org/img/wn/${day.weather[0].icon}@2x.png`}
        alt={day.weather[0].description}
      />
      <p>{day.weather[0].description}</p>
      <p>Max: {maxTemp}°C</p>
      <p>Min: {minTemp}°C</p>
    </div>
  );
};

export default WeatherCard;
