// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css'
function App() {
  const [weatherData, setWeatherData]= useState(null);
  const [location, setLocation] = useState('');
  const apiKey = process.env.REACT_APP_WEATHER_API_KEY

  useEffect(()=>{
    const fetchData = async ()=>{
      try {
        const response = await axios.get(`http://api.weatherapi.com/v1/forecast.json?key=${apiKey}&q=${location}&days=7&aqi=yes&alerts=yes`);
        setWeatherData(response);
        console.log(weatherData);
        console.log(response.data);

      } catch (error) {
        console.log(error);
      }
    };

    if(location){
      fetchData();
    }
  },[location]);

  const handleLocationChange = (event)=>{
    setLocation(event.target.value);
  }
  return (
   <>
<div className='app-container'>
  <h1 className='app-title '>Hava Durumu</h1>
  <div className='input-container'>
    <input className='location-input' type='text' placeholder='Åžehir Giriniz' value={location} onChange={handleLocationChange} />
  </div>

</div>

{weatherData && weatherData.forecast && weatherData.forecast.forecastday && (
    <div className='weather-container'>
        {weatherData.forecast.forecastday.map((day) => (
            <div className='day-container' key={day.date}>
                <h2>{day.date}</h2>
                <img
                    className='weather-icon'
                    src={day.day.condition.icon}
                    alt={day.day.condition.text} 
                />
                <h2>{day.day.condition.text} </h2>
            </div>
        ))}
    </div>
)}
   </>
  );
}

export default App;
