import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const WeatherContext = createContext();

export const WeatherProvider = ({ children }) => {
  const [city, setCity] = useState('Istanbul'); // Default city
  const [forecast, setForecast] = useState(null); // Change to null for better checks
  const [error, setError] = useState(null); // To handle errors
  const apiKey = process.env.REACT_APP_OPENWEATHER_API_KEY;

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const response = await axios.get(
          `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`
        );
        console.log("API Response:", response.data);
        setForecast(response.data); // Store the full response data
        setError(null); // Reset error if successful
      } catch (error) {
        console.error("Error fetching weather data", error);
        setError("Could not fetch weather data. Please try again later."); // Set error message
      }
    };
    fetchWeather();
  }, [city]);

  return (
    <WeatherContext.Provider value={{ forecast, setCity, error }}>
      {children}
    </WeatherContext.Provider>
  );
};
