// src/WeatherDashboard.js
import React, { useContext, useState } from 'react';
import { WeatherContext } from './WeatherContext';


const cities = ['Istanbul', 'Ankara', 'Izmir', 'Bursa', 'Adana'];  // Şehir listesi

const WeatherDashboard = () => {
  const { forecast, setCity } = useContext(WeatherContext);
  const [selectedCity, setSelectedCity] = useState('Istanbul');

  const handleCityChange = (e) => {
    const city = e.target.value;
    setSelectedCity(city);
    setCity(city);
  };

  return (
    <div>
      <h1>Hava Durumu</h1>
      <select value={selectedCity} onChange={handleCityChange}>
        {cities.map((city) => (
          <option key={city} value={city}>
            {city}
          </option>
        ))}
      </select>

      <div className="forecast">
        {forecast.map((day, index) => (
          <WeatherCard key={index} day={day} isToday={index === 0} />
        ))}
      </div>
    </div>
  );
};

const WeatherCard = ({ day, isToday }) => {
  const date = new Date(day.dt * 1000).toLocaleDateString('tr-TR', { weekday: 'long' });
  const maxTemp = Math.round(day.temp.max);
  const minTemp = Math.round(day.temp.min);

  return (
    <div className={`weather-card ${isToday ? 'today' : ''}`}>
      <h3>{date}</h3>
      <img
        src={`http://openweathermap.org/img/wn/${day.weather[0].icon}@2x.png`}
        alt={day.weather[0].description}
      />
      <p>{day.weather[0].description}</p>
      <p>Max: {maxTemp}°C</p>
      <p>Min: {minTemp}°C</p>
    </div>
  );
};

export default WeatherDashboard;
