// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css'
function App() {
  const [weatherData, setWeatherData]= useState(null);
  const [location, setLocation] = useState('');

  useEffect(()=>{
    const fetchData = async ()=>{
      try {
        const response = await axios.get(`http://api.weatherapi.com/v1/forecast.json?key=${import.meta.env.REACT_APP_WEATHER_API_KEY}&q=${location}&days=7&aqi=yes&alerts=yes`);
        setWeatherData(response);
        console.log(response.data);

      } catch (error) {
        console.log(error);
      }
    };
    if(location){
      fetchData();
    }
  },[location]);

  const handleLocationChange = (event)=>{
    setLocation(event)
  }
  return (
   <>
<div className='app-container'>
  <h1 className='app-title '>Hava Durumu</h1>
  <div className='input-container'>
    <input className='location-input' type='text' placeholder='Åžehir Giriniz' value={location} onChange={handleLocationChange} />
  </div>
</div>
   </>
  );
}

export default App;
