// src/WeatherContext.js
import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const WeatherContext = createContext();

export const WeatherProvider = ({ children }) => {
  const [city, setCity] = useState('Istanbul'); // Varsayılan şehir
  const [forecast, setForecast] = useState([]);
  const apiKey = '22a59f0d8e60a92a29a6235219c44f33'; // OpenWeather API anahtarınızı buraya ekleyin

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const response = await axios.get(
          `https://api.openweathermap.org/data/2.5/forecast/daily?q=${city}&cnt=7&appid=${apiKey}&units=metric`
        );
        console.log("API Response:", response.data);
        setForecast(response.data.list);
      } catch (error) {
        console.error("Error fetching weather data", error);
      }
    };
    fetchWeather();
  }, [city]);

  return (
    <WeatherContext.Provider value={{ forecast, setCity }}>
      {children}
    </WeatherContext.Provider>
  );
};
