import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const WeatherContext = createContext();

export const WeatherProvider = ({ children }) => {
  const [city, setCity] = useState('Istanbul'); // Default city
  const [forecast, setForecast] = useState([]);
  const apiKey = process.env.REACT_APP_OPENWEATHER_API_KEY;
   // Şehir koordinatlarını tanımlayın
   const cityCoordinates = {
    Istanbul: { lat: 41.0082, lon: 28.9784 },
    Ankara: { lat: 39.9334, lon: 32.8597 },
    Izmir: { lat: 38.4192, lon: 27.1287 },
    Bursa: { lat: 40.1826, lon: 29.0669 },
    Adana: { lat: 37.0019, lon: 35.3285 },
  };


  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const response = await axios.get(
            `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&exclude=current,minutely,hourly,alerts&appid=${apiKey}&units=metric`
          );
        console.log("API Response:", response.data);
        setForecast(response.data.list); // Update to set the forecast list
      } catch (error) {
        console.error("Error fetching weather data", error);
      }
    };
    fetchWeather();
  }, [city]);

  return (
    <WeatherContext.Provider value={{ forecast, setCity }}>
      {children}
    </WeatherContext.Provider>
  );
};
